import React, { Component } from './node_modules/react';
import { M } from './node_modules/materialize-css/dist/js/materialize.min.js.js';

import NSA from "../../pages/images/nsaBuild.jpg";
import doctor from "../../pages/images/doctor.jpg";
import trivia from "../../pages/images/triviaChuck.jpg";
import biohazzard from "../../pages/images/biohazzard.jpg";
import liri from "../../pages/images/liri.jpg";
import claws from "../../pages/images/claws.jpg";

export default class Slider extends Component {
  componentDidMount() {
   var elem = document.querySelector('.carousel');
   var instance = M.Carousel.init(elem, { duration: 200 });
 }
 componentDidUpdate() {

    let collapsible = document.querySelectorAll(".collapsible");

    M.Collapsible.init(collapsible, {});
  }
  
render() {
 return (
  <div className="container center-align">
   <h1 className="header pink-text">Slider</h1>
    <div className="carousel">
      <a className="carousel-item" href="#one!">
        <img src={NSA} />
      </a>
      <a className="carousel-item" href="#two!">
        <img src={doctor} />
      </a>
      <a className="carousel-item" href="#three!">
       <img src={trivia} />
      </a>
      <a className="carousel-item" href="#four!">
       <img src={biohazzard} />
      </a>
       <a className="carousel-item" href="#five!">
        <img src={liri} />
       </a>
       <a className="carousel-item" href="#five!">
        <img src={claws} />
       </a>
     </div>
    </div>
  );
 }
}
